mapper.insert:

    	select seq_webtoon.nextval from dual

	insert into tbl_webtoon(
		webtoon_id
		,title
		,summary
		,author_id
		,hashtag

		,thumbnail

	)values(
		#{webtoonId}
		,#{title}
		,#{summary}
		,#{authorId}
		,#{hashtag}

		,#{thumbnail}

	)

mapper.select:
select * from tbl_webtoon


	author_id = #{authorId}


	author_id = (
		select user_id 
		from tbl_user
		where nickname = #{author}
	)


	and title like '%'||#{title}||'%'



	and hashtag like '%'||#{hashtag}||'%'

select count(*) from tbl_webtoon


	author_id = #{authorId}


	author_id = (
		select user_id 
		from tbl_user
		where nickname = #{author}
	)


	and title like '%'||#{title}||'%'



	and hashtag like '%'||#{hashtag}||'%'
